      }
    }

    &-links {
      display: flex;
      text-align: center;

      &__link {
        svg {
          @media screen and (max-width: 768px) {
            min-width: 24px;
          }
        }
        &:hover {
          path {
            fill: #E11C4B;
          }

          span {
            color: #E11C4B;
          }
        }
      }

      &__name {
        color: #333333;
        @media screen and (max-width: 768px) {
          display: none;
        }
      }

      &__item {

        &:not(:last-child) {
        margin-right: 24px;
      }
      }
    }

